const fs = require('fs');
const path = require('path');

// Read the president file
const filePath = path.join(process.cwd(), 'current_president.txt');
let fileContent;

try {
  fileContent = fs.readFileSync(filePath, 'utf8');
  console.log('Successfully read current_president.txt');
  console.log('File content:', fileContent);
} catch (error) {
  console.error('Error reading file:', error.message);
  result = { success: false, error: error.message };
  return;
}

// Parse the information from the file
const lines = fileContent.split('\n');
const presidentInfo = {};

for (const line of lines) {
  if (line.includes('Name:')) {
    presidentInfo.name = line.split('Name:')[1].trim();
  } else if (line.includes('Term:')) {
    presidentInfo.term = line.split('Term:')[1].trim();
  } else if (line.includes('Party:')) {
    presidentInfo.party = line.split('Party:')[1].trim();
  } else if (line.includes('Source:')) {
    presidentInfo.source = line.split('Source:')[1].trim();
  }
}

console.log('Parsed president info:', presidentInfo);

// Create verification report
const verificationReport = {
  timestamp: new Date().toISOString(),
  fileVerified: 'current_president.txt',
  extractedInfo: presidentInfo,
